import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, FlatList, Image, TouchableOpacity, ScrollView } from "react-native";
import Navigator from "../../component/navigative";

import { getData, storeData } from "../../component/store";
import { insertCart, dropTableCart, createTableCart } from "../../db/cart";
import { findProduct } from "../../db/product";


export default function ShoppingCartScreen({ navigation }) {
    const [product, setProduct] = useState(null);
    const [products, setProducts] = useState(null);
    const [user, setUser] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [isAddingToCart, setIsAddingToCart] = useState(false);

    useEffect(() => {
        fetchData();
    }, [product]);

    useEffect(() => {
        getuser();
    }, [user]);

    const getuser = async () => {
        try {
            const data_user = await getData("@user");
            setUser(data_user);
        } catch (error) {
            console.error("Error retrieving data:", error);
        }
    }

    const fetchData = async () => {
        try {
            const data = await getData("@product");
            const data_user = await getData("@user");
            const data_2 = await findProduct(data.productName, 30)
            setProduct(data);
            setUser(data_user);
            setProducts([...data_2])
        } catch (error) {
            console.error("Error retrieving data:", error);
        } finally {
            setIsAddingToCart(false);
        }
    };

    const addToCart = async (item) => {
        try {
            const { describe, link_img, price, productName, sale_id } = item;
            insertCart(productName, price, describe, link_img, quantity,  sale_id, user.id );
            
            alert("done");
        } catch (e) {
            alert('Vui lòng thử lại.');
            console.log(e);
        }
    };

    const renderItem = ({ item }) => (
        <TouchableOpacity style={styles.productItem} onPress={() => {
            storeData("@product", item);
            setProduct(null);
            setIsAddingToCart(true);
        }}>
            <Image source={{ uri: item.link_img }} style={styles.productImage} />
            <Text style={styles.productName}>{item.productName}</Text>
            <Text style={styles.productDescription} numberOfLines={2}>
                {item.describe.length > 30 ? item.describe.substring(0, 30) + "..." : item.describe}
            </Text>
            <Text style={styles.productPrice}>{item.price}</Text>
        </TouchableOpacity>
    );

    return (
        <>
            <ScrollView>
                <View style={styles.container}>
                    {product != null ? (
                        <>
                            <Text style={styles.title}>{product.productName}</Text>
                            <View style={styles.content}>
                                <View key={product.id} style={styles.productContainer}>
                                    <Image source={{ uri: product.link_img }} style={styles.productImage} />
                                    <Text style={styles.productPrice}>Price: {product.price}</Text>

                                    <View style={styles.quantityContainer}>
                                        <View style={styles.quantityButtonContainer}>
                                            <TouchableOpacity
                                                style={styles.quantityButton}
                                                onPress={() => setQuantity(quantity - 1)}
                                                disabled={quantity === 1}
                                            >
                                                <Text style={styles.quantityButtonText}>-</Text>
                                            </TouchableOpacity>
                                            <Text style={styles.quantityText}>{quantity}</Text>
                                            <TouchableOpacity
                                                style={styles.quantityButton}
                                                onPress={() => setQuantity(quantity + 1)}
                                            >
                                                <Text style={styles.quantityButtonText}>+</Text>
                                            </TouchableOpacity>
                                        </View>
                                        <TouchableOpacity style={styles.buyButton}>
                                            <Text style={styles.buyButtonText} onPress={() => addToCart(product)}>Add to Cart</Text>
                                        </TouchableOpacity>
                                    </View>
                                </View>
                            </View>

                            {products != null ? (
                                <ScrollView>
                                    <Text style={styles.sectionTitle}>Sản phẩm tương tự</Text>
                                    <FlatList
                                        data={products}
                                        renderItem={renderItem}
                                        keyExtractor={(item) => item.id}
                                        numColumns={2}
                                        contentContainerStyle={styles.productList}
                                    />
                                </ScrollView>
                            ) : (
                                <Text></Text>
                            )}

                        </>
                    ) : (
                        <Text>Loading...</Text>
                    )}
                </View>
            </ScrollView>
            <Navigator />
        </>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginTop: 20,
    },
    title: {
        fontSize: 24,
        fontWeight: "bold",
        textAlign: "center",
        marginBottom: 20,
    },
    content: {
        flex: 1,
        alignItems: "center",
    },
    productContainer: {
        alignItems: "center",
        marginBottom: 20,
    },
    productName: {
        fontSize: 18,
        fontWeight: "bold",
        textAlign: "center",
    },
    productPrice: {
        fontSize: 26,
        fontWeight: "bold",
        color: "green",
        marginRight: 5,
        alignItems: "center",
    },
    quantityContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 8,
    },
    quantityButtonContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginRight: 8,
    },
    quantityButton: {
        backgroundColor: '#DDDDDD',
        paddingHorizontal: 12,
        paddingVertical: 8,
        borderRadius: 4,
    },
    quantityButtonText: {
        fontSize: 16,
        fontWeight: 'bold',
    },
    quantityText: {
        fontSize: 16,
        fontWeight: 'bold',
        marginHorizontal: 8,
    },
    buyButton: {
        backgroundColor: 'blue',
        paddingHorizontal: 16,
        paddingVertical: 12,
        borderRadius: 4,
    },
    buyButtonText: {
        color: 'white',
        fontSize: 16,
        fontWeight: 'bold',
    },
    sectionTitle: {
        fontSize: 20,
        fontWeight: "bold",
        marginTop: 20,
        marginBottom: 10,
        marginLeft: 10,
    },
    productList: {
        paddingHorizontal: 10,
    },
    productItem: {
        flex: 1,
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        margin: 5,
        padding: 10,
        backgroundColor: "#f3f3f3",
        borderRadius: 5,
    },
    productImage: {
        width: 150,
        height: 150,
        resizeMode: "cover",
        marginBottom: 5,
    },
    productName: {
        fontSize: 16,
        fontWeight: "bold",
        textAlign: "center",
    },
    productDescription: {
        fontSize: 12,
        textAlign: "center",
        marginBottom: 5,
    },
    productPrice: {
        fontSize: 14,
        fontWeight: "bold",
        color: "green",
    },
});
